import{_ as s}from"./plugin-vue_export-helper-c27b6911.js";import{r as n,o,c as d,d as c,a as t,e as a,w as l,b as e,f as u}from"./app-cdaf2cd7.js";const h="/assets/images/amnos_cic/cic_usb0001_ch340x_01.jpg",f="/assets/images/amnos_cic/cic_usb0001_ch340x_00.jpg",p="/assets/images/amnos_cic/cic_usb0001_ch340x_02.png",g={},x={class:"table-of-contents"},m=u('<h2 id="overview" tabindex="-1"><a class="header-anchor" href="#overview" aria-hidden="true">#</a> Overview</h2><p>CH340 is a USB bus converter chip which converts USB to serial port or printer port. In UART mode, CH340 provides standard MODEM signals, used to extend serial port for computers, or upgrade directly from normal serial device to USB bus.</p><figure><img src="'+h+'" alt="cic-usb0001-ch340x-breakout-top" tabindex="0" loading="lazy"><figcaption>cic-usb0001-ch340x-breakout-top</figcaption></figure><figure><img src="'+f+'" alt="cic-usb0001-ch340x-breakout-bottom" tabindex="0" loading="lazy"><figcaption>cic-usb0001-ch340x-breakout-bottom</figcaption></figure><h2 id="features" tabindex="-1"><a class="header-anchor" href="#features" aria-hidden="true">#</a> Features</h2><ul><li>Full-speed USB device interface, USB 2.0 compatible.</li><li>Emulate standard UART interface, used to upgrade the original serial peripheral or expand additional UART via USB.</li><li>Original UART applications are totally compatible without any modification in Windows operating systems.</li><li>Hardware full duplex UART interface, integrated transmit-receive buffer, supports communication baud rate varies from 50bps to 2Mbps.</li><li>Supports 5V and 3.3V power supply voltages.</li></ul><h2 id="specifications" tabindex="-1"><a class="header-anchor" href="#specifications" aria-hidden="true">#</a> Specifications</h2><ul><li>AW9523B - a 16 multi-function LED driver and GPIO controller</li><li>Headers for P0, P1, VDD, GND, easy to use</li><li>Headers for I2C, VDD, GND, Reset &amp; AD0/1, easy to connect with MCU</li><li>Qwiic connector x 2, easy to debug &amp; use</li><li>Dimension: 44 x 18 x 5 mm</li></ul><h2 id="hardware" tabindex="-1"><a class="header-anchor" href="#hardware" aria-hidden="true">#</a> Hardware</h2><figure><img src="'+p+`" alt="cic-usb0001-ch340x-breakout-pinout" tabindex="0" loading="lazy"><figcaption>cic-usb0001-ch340x-breakout-pinout</figcaption></figure><table><thead><tr><th style="text-align:left;">Pin</th><th style="text-align:left;">Description</th><th style="text-align:center;">Note</th></tr></thead><tbody><tr><td style="text-align:left;">5V0</td><td style="text-align:left;">Power Suppy 5.0V</td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;">GND</td><td style="text-align:left;">Ground</td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;">3V3</td><td style="text-align:left;">Power Suppy 3.3V, Max 0.8A</td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;">GND</td><td style="text-align:left;">Ground</td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;">TXD</td><td style="text-align:left;">UART interface Serial Data output pin</td><td style="text-align:center;">Connected with ESP32&#39;s RX</td></tr><tr><td style="text-align:left;">RXD</td><td style="text-align:left;">UART interface Serial Data input pin</td><td style="text-align:center;">Connected with ESP32&#39;s TX</td></tr><tr><td style="text-align:left;">BOOT</td><td style="text-align:left;">Connected with MCU Boot Control Pin</td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;">EN</td><td style="text-align:left;">Hardware Reset Output Pin, Low Reset</td><td style="text-align:center;">Connected with ESP32&#39;s EN</td></tr></tbody></table><h2 id="user-guide" tabindex="-1"><a class="header-anchor" href="#user-guide" aria-hidden="true">#</a> User Guide</h2><p>CIC CH340X Breakout is used to program STM32, CH32Vxx, ESP32 &amp; ESP8266.</p><h3 id="examples" tabindex="-1"><a class="header-anchor" href="#examples" aria-hidden="true">#</a> Examples</h3><p><strong>Program ESP32</strong></p><p>Please connect the wire correctly as below:</p><table><thead><tr><th style="text-align:left;">CH340X</th><th style="text-align:left;">ESP32</th></tr></thead><tbody><tr><td style="text-align:left;">3V3</td><td style="text-align:left;">3V3</td></tr><tr><td style="text-align:left;">GND</td><td style="text-align:left;">GND</td></tr><tr><td style="text-align:left;">TXD</td><td style="text-align:left;">RXD</td></tr><tr><td style="text-align:left;">RXD</td><td style="text-align:left;">TXD</td></tr><tr><td style="text-align:left;">BOOT</td><td style="text-align:left;">BOOT</td></tr><tr><td style="text-align:left;">EN</td><td style="text-align:left;">EN</td></tr></tbody></table><p>After USB Cable of CH340X Breakout connecting to PC, there is a <code>/dev/ttyUSB0</code> serial device found. Use Command as below to program firmware:</p><div class="language-text line-numbers-mode" data-ext="text"><pre class="language-text"><code>idf.py -p /dev/ttyUSB0 flash monitor
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>The firmware will be programmed automaticly.</p><h2 id="buy-it-now" tabindex="-1"><a class="header-anchor" href="#buy-it-now" aria-hidden="true">#</a> Buy it Now</h2>`,21),_={href:"https://www.analoglamb.com",target:"_blank",rel:"noopener noreferrer"},y=t("h2",{id:"resources",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#resources","aria-hidden":"true"},"#"),e(" Resources")],-1),b=t("h3",{id:"documents",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#documents","aria-hidden":"true"},"#"),e(" Documents")],-1),w={href:"https://www.wch-ic.com/products/CH340.html?",target:"_blank",rel:"noopener noreferrer"},v=t("br",null,null,-1),D=t("a",{href:"/files/schematic/schematic__cic_usb0001_ch340x_breakout_v10.pdf"},"Schematic",-1);function S(C,B){const i=n("router-link"),r=n("ExternalLinkIcon");return o(),d("div",null,[c(" more "),t("nav",x,[t("ul",null,[t("li",null,[a(i,{to:"#overview"},{default:l(()=>[e("Overview")]),_:1})]),t("li",null,[a(i,{to:"#features"},{default:l(()=>[e("Features")]),_:1})]),t("li",null,[a(i,{to:"#specifications"},{default:l(()=>[e("Specifications")]),_:1})]),t("li",null,[a(i,{to:"#hardware"},{default:l(()=>[e("Hardware")]),_:1})]),t("li",null,[a(i,{to:"#user-guide"},{default:l(()=>[e("User Guide")]),_:1}),t("ul",null,[t("li",null,[a(i,{to:"#examples"},{default:l(()=>[e("Examples")]),_:1})])])]),t("li",null,[a(i,{to:"#buy-it-now"},{default:l(()=>[e("Buy it Now")]),_:1})]),t("li",null,[a(i,{to:"#resources"},{default:l(()=>[e("Resources")]),_:1}),t("ul",null,[t("li",null,[a(i,{to:"#documents"},{default:l(()=>[e("Documents")]),_:1})])])])])]),m,t("p",null,[t("a",_,[e("AnalogLamb.Com"),a(r)])]),y,b,t("p",null,[t("a",w,[e("CH340X Datasheet"),a(r)]),v,D])])}const N=s(g,[["render",S],["__file","cic_usb0001_ch340x.html.vue"]]);export{N as default};
